import React, { useState, useEffect } from "react";
import { database } from './FirebaseConfig';
import { useNavigate } from "react-router-dom";
import { signOut, currentUser } from "firebase/auth";

function Home() {
    const [userProfile, setUserProfile] = useState(null);
    const history = useNavigate();

    // Fetch user profile data when component mounts
    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                const user = currentUser(database);
                // Example: Fetch user profile from Firestore
                const userProfileData = await fetchUserProfileFromFirestore(user.uid);
                setUserProfile(userProfileData);
            } catch (error) {
                console.error("Error fetching user profile:", error);
            }
        };
        fetchUserProfile();
    }, []);

    const fetchUserProfileFromFirestore = async (userId) => {
        // Example: Fetch user profile data from Firestore based on userId
        // Replace this with your own Firestore query
        // For example:
        // const userProfileRef = collection(database, 'users').doc(userId);
        // const userProfileSnapshot = await getDoc(userProfileRef);
        // return userProfileSnapshot.data();
        return {
            // Example user profile data
            displayName: "John Doe",
            email: "john@example.com",
            // Add more profile data as needed
        };
    };

    const handleClickSignOut = () => {
        signOut(database)
            .then(() => {
                console.log("User signed out successfully");
                history('/');
            })
            .catch((error) => {
                console.error("Error signing out:", error);
            });
    };

    return (
        <div className="home-container">
            <div className="home-content">
                <h1>Welcome, {userProfile ? userProfile.displayName : "Guest"}</h1>
                {/* Display additional user profile information */}
                {userProfile && (
                    <div>
                        <p>Email: {userProfile.email}</p>
                        {/* Add more profile information as needed */}
                    </div>
                )}
                <div className="vision-mission">
                    <h2>Vision</h2>
                    <p>"To pioneer a future where chronic kidney disease is effectively managed through cutting-edge predictive technologies, fostering proactive healthcare and enhancing overall well-being."</p>
                    <h2>Mission</h2>
                    <p>"Our mission is to revolutionize the landscape of chronic kidney disease management by delivering state-of-the-art prediction models. Through continuous innovation and collaboration with healthcare professionals, our goal is to empower individuals with timely insights, enabling early intervention and personalized care pathways. We strive to make a lasting impact on global health by reducing the burden of chronic kidney disease and improving outcomes for all."</p>
                </div>
            </div>
            <div>
                <h1>Home</h1>
                <button onClick={handleClickSignOut}>Sign Out</button>
            </div>
        </div>
    );
}

export default Home;
