import React, { useState } from "react";
import axios from 'axios';
import { useNavigate } from "react-router-dom";

import './LoginPage.css'; // Import custom CSS file for styling





export default function LoginPage() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();
     
    const logInUser = () => {
        if(email.length === 0){
            alert("Email has been left blank!");
        } else if(password.length === 0){
            alert("Password has been left blank!");
        } else {
            axios.post('http://127.0.0.1:5000/login', {
                email: email,
                password: password
            })
            .then(function (response) {
                console.log(response);
                navigate("/pages/home");
            })
            .catch(function (error) {
                console.log(error, 'error');
                if (error.response.status === 401) {
                    alert("Invalid credentials");
                }
            });
        }
    }
    
    return (
        <div className="container h-100">
             <img src="/kidneys.jpeg" alt="Kidneys" className="landing-image" />
            <div className="row justify-content-center align-items-center h-100">
                <div className="col-md-6 col-lg-5 col-xl-4">
                    {/* <img src={imgs[0]} className="img-fluid" alt="Login Image" /> */}
                </div>
                <div className="col-md-6 col-lg-5 col-xl-4">
                    <form className="login-form">
                        <h2 className="form-title">Log Into Your Account</h2>
                        <div className="form-group">
                            <label htmlFor="email" className="form-label">Email address</label>
                            <input
                                type="email"
                                id="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                className="form-control"
                                placeholder="Enter a valid email address"
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password" className="form-label">Password</label>
                            <input
                                type="password"
                                id="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                className="form-control"
                                placeholder="Enter password"
                            />
                        </div>
                        <div className="form-check">
                            <input className="form-check-input" type="checkbox" id="rememberMe" />
                            <label className="form-check-label" htmlFor="rememberMe">Remember me</label>
                        </div>
                        <a href="#!" className="forgot-password">Forgot password?</a>
                        <button type="button" className="btn btn-primary" onClick={logInUser}>Login</button>
                        <p className="register-link">Don't have an account? <a href="/register">Register here</a></p>
                    </form>
                </div>
            </div>
        </div>



    );
}
