import React, { useState } from 'react';
import { auth } from '../firebase';
import { useEffect } from 'react'
import { jwtDecode } from 'jwt-decode';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { Link, useNavigate } from 'react-router-dom';
import './styles.css';

const Login = () => {
  const [email, setEmail] = useState(''); // Declare and initialize email state
  const [password, setPassword] = useState(''); // Declare and initialize password state
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      console.log("User signed in:", userCredential.user);
      localStorage.setItem('token', userCredential.user.accessToken);
      localStorage.setItem('user', JSON.stringify(userCredential.user));
      navigate("/");
    } catch (error) {
      console.error("Sign-in error:", error.code, error.message);
    }
  }

  const [ user, setUser ] = useState({});

  function handleCallbackResponse(response) {
    console.log("Encoded JWT ID token: " + response.credential);
    var userObject = jwtDecode(response.credential);
    console.log(userObject);
    setUser(userObject);
    document.getElementById("signInDiv").hidden = true;
  }
  
  function handleSignOut(event){
    setUser({});
    document.getElementById("signInDiv").hidden = false;
  }

  useEffect(() => {
   /* global google */
   google.accounts.id.initialize({
     client_id: "68699901023-rh4pvjqtvdv1ttgs44cio9pbogvue0du.apps.googleusercontent.com",
    callback: handleCallbackResponse
  });
  
   google.accounts.id.renderButton(
     document.getElementById("signInDiv"),
     { theme: "outline", size: "large"}
  );
  }, []);

  return (
    <div className="container"> {/* Added container with gradient background */}
      <h1>Chronic Kidney Prediction System</h1> {/* Added header */}
      <h2>Login Page</h2> {/* Existing header */}
      <form onSubmit={handleSubmit} className='login-form'>
        <input
          type="email"
          placeholder="Your Email"
          required
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Your Password"
          required
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit" className='login-button'>Login</button>
        <div className="App">
        <div id="signInDiv"></div>
        {
          Object.keys(user).length !== 0 &&
          <button onClick={(e) => handleSignOut(e)}>Sign Out</button>
        }
      

        <p>Need to Signup? <Link to="/signup">Create Account</Link></p>
      </div>
      </form>
      <div className="App">
        <div id="signInDiv"></div>
        {
        Object.keys(user).length != 0 &&
        <button onClick={(e) => handleSignOut(e)}>Sign Out</button>
      }

      </div>
      
    </div>
  )
}

export default Login;
