import React, { useState } from 'react';
import { signOut } from "firebase/auth";
import { database } from './FirebaseConfig';
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import './prediction.css'; 
import Navbar from "../Navbar"

export default function Prediction() {

  const history = useNavigate()
  const handleClick = () =>{
      signOut(database).then(val=>{
          console.log(val,"val")
          history('/')
      })
  }


  const [prediction, setPrediction] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const data = Object.fromEntries(formData.entries());

    try {
      const response = await axios.post('http://localhost:5000/predict', data);
      setPrediction(response.data.prediction === 1 ? 'The models analysis indicates a high likelihood of chronic kidney disease based on the presented data.' : 'This statement from the model implies that, based on the input data it received, it concludes that the patient is unlikely to have chronic kidney disease. However, its essential to remember that model predictions are not definitive diagnoses and should be interpreted with caution. If there are concerns about kidney health, consulting a healthcare professional for further evaluation is advisable.');
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleClear = (e) => {
    e.preventDefault();

    document.querySelectorAll('input').forEach(input => input.value = '');

    document.querySelector('select').selectedIndex = 0;

    setPrediction('');
  };

  return (
   <>
    <Navbar />

    <div className="Container"> 
    <div className="b-container">
        <button onClick={handleClick} className="sign-out-btn">Sign Out</button>
    </div>
        <div className="prediction-container">
          <h1>Chronic Kidney Disease Prediction System</h1>
            <ul className="bullet-list">
              <li className="bullet-item">Discover valuable insights into your kidney health with our advanced prediction tool.</li>
              <li className="bullet-item">Gain a deeper understanding of your kidney function and potential risks.</li>
              <li className="bullet-item">Ready to take control of your kidney health? Start by completing our comprehensive questionnaire.</li>
          </ul>
        </div>

      <form onSubmit={handleSubmit}>
        <label htmlFor="age">Age:</label>
        <input type="number" name="age" min="1" max="120" required />
        <label htmlFor="al">Albumin level in Urine:</label>
        <input type="number" name="al" step="0.1" min="0.0" max="5.0" required />

        {/* Other form fields */}

        <label htmlFor="htn">Hypertension:</label>
        <select name="htn" required>
          <option value="yes">Yes</option>
          <option value="no">No</option>
        </select>

        <button type="submit">Predict</button>
        <button onClick={handleClear}>Clear</button>
      </form>
      <div className="result">{prediction}</div>
    </div>
  </>
  );
}
