import React from 'react';
import { Route, Navigate } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Assuming you have an AuthContext to manage authentication

function PrivateRoute({ element, ...rest }) {
  const { currentUser } = useAuth(); // Get the current user from the AuthContext

  return (
    <Route
      {...rest}
      element={currentUser ? element : <Navigate to="/" replace />} // Redirect to login if user is not authenticated
    />
  );
}

export default PrivateRoute;
