import React, { useState } from 'react';
import { auth } from '../firebase';
import { signInWithEmailAndPassword } from 'firebase/auth';
import { Link, useNavigate } from 'react-router-dom';
import './styles.css';

const Login = () => {
  const [email, setEmail] = useState(''); // Declare and initialize email state
  const [password, setPassword] = useState(''); // Declare and initialize password state
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await signInWithEmailAndPassword(auth, email, password);
      console.log("User signed in:", userCredential.user);
      localStorage.setItem('token', userCredential.user.accessToken);
      localStorage.setItem('user', JSON.stringify(userCredential.user));
      navigate("/");
    } catch (error) {
      console.error("Sign-in error:", error.code, error.message);
    }
  }

  return (
    <div className="container"> {/* Added container with gradient background */}
      <h1>Chronic Kidney Prediction System</h1> {/* Added header */}
      <h2>Login Page</h2> {/* Existing header */}
      <form onSubmit={handleSubmit} className='login-form'>
        <input
          type="email"
          placeholder="Your Email"
          required
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Your Password"
          required
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit" className='login-button'>Login</button>
      </form>
      <p>Need to Signup? <Link to="/signup">Create Account</Link></p>
    </div>
  )
}

export default Login;
