import React, { useState } from "react";
import { database } from "./FirebaseConfig";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
} from "firebase/auth";
import { useNavigate } from "react-router-dom";
import './PasswordLoginWithFirebase.css'

function RegisterAndLogin() {
  const [login, setLogin] = useState(false);

  const history = useNavigate();

  const handleSubmit = (e, type) => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;
    if (type === "signup") {
      createUserWithEmailAndPassword(database, email, password)
        .then((data) => {
          console.log(data, "authData");
          history("/home");
        })
        .catch((err) => {
          alert(err.code);
          setLogin(true);
        });
    } else {
      signInWithEmailAndPassword(database, email, password)
        .then((data) => {
          console.log(data, "authData");
          history("/home");
        })
        .catch((err) => {
          alert(err.code);
        });
    }
  };

  const handleReset = () => {
    history("/reset");
  };

  return (
    <div>
    <h1>Chronic Kidney Disease Prediction System</h1>
    <p align='center'>We provide advanced prediction models to assess the risk of chronic kidney disease.</p>
    <div className="container">
      <div className="row">
        <div
          className={login ? "col-md-6 activeColor" : "col-md-6 pointer"}
          onClick={() => setLogin(false)}
        >
          SignUp
        </div>
        <div
          className={login ? "col-md-6 activeColor" : "col-md-6 pointer"}
          onClick={() => setLogin(true)}
        >
          SignIn
        </div>
      </div>
      <h1 className="text-center">{login ? "SignIn" : "SignUp"}</h1>
      <form onSubmit={(e) => handleSubmit(e, login ? "signin" : "signup")}>
        <input className="form-control" name="email" placeholder="Email" />
        <br />
        <input className="form-control" name="password" type="password" placeholder="Password" />
        <br />
        <p className="text-center" onClick={handleReset}>Forgot Password?</p>
        <br />
        <button className="btn btn-primary btn-block">{login ? "SignIn" : "SignUp"}</button>
      </form>
    </div>
    </div>
  );
}

export default RegisterAndLogin;
