import React, { useState } from "react";
import { database } from "./FirebaseConfig";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
} from "firebase/auth";
import { useNavigate } from "react-router-dom";
import "./RegisterAndLogin.css";

function RegisterAndLogin() {
  const [login, setLogin] = useState(false);
  const [loading, setLoading] = useState(false);
  const history = useNavigate();

  const handleSignUp = () => {
    setLogin(false);
  };

  const handleSignIn = () => {
    setLogin(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;

    setLoading(true);

    try {
      if (login) {
        await signInWithEmailAndPassword(database, email, password);
      } else {
        await createUserWithEmailAndPassword(database, email, password);
      }
      history("/home");
    } catch (error) {
      alert(error.code);
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    history("/reset");
  };

  return (
    <div className="register-and-login-container">
      <h1>Chronic Kidney Disease Prediction System</h1>
      <p className="text-center">
        We provide advanced prediction models to assess the risk of chronic
        kidney disease.
      </p>

      <div className="buttons-container">
        <button
          className={login ? "button" : "button activeColor"}
          onClick={handleSignUp}
        >
          SignUp
        </button>
        <button
          className={login ? "button activeColor" : "button"}
          onClick={handleSignIn}
        >
          SignIn
        </button>
      </div>
      <h1 className="text-center">{login ? "SignIn" : "SignUp"}</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Email</label>
          <input className="form-control" name="email" placeholder="Email" />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            className="form-control"
            name="password"
            type="password"
            placeholder="Password"
          />
        </div>
        <p className="text-center" onClick={handleReset}>
          Forgot Password?
        </p>
        <button className="btn btn-primary btn-block" disabled={loading}>
          {loading ? "Loading..." : login ? "SignIn" : "SignUp"}
        </button>
      </form>
    </div>
  );
}

export default RegisterAndLogin;
